xpath.script:
            $(document).ready(function(){
            var xhr;
            var state_id, $state_id;
            var city_id, $city_id;
            var subdistrict_id, $subdistrict_id;
            var kelurahan_id, $kelurahan_id;

            $country_id = $('#country_id').selectize({
                valueField: 'id',
                labelField: 'name',
                searchField: 'name',
                options: [{"id":"", "name":""}],
                create: false,
                persist: false,

                load: function(query, callback) {
                    if (!query.length) return callback();
                    $.ajax({
                        url: '/selectize/countries',
                        type: 'GET',
                        dataType: 'json',
                        data: {
                            q: query,
                            page_limit: 10,
                        },
                        error: function(res) {
                            console.log(res);
                        },
                        success: function(res) {
                            callback(res);
                        }
                    });
                },
                onChange: function(value) {

                    if (!value.length) return;

                    state_id.disable();
                    state_id.clearOptions();

                    city_id.disable();
                    city_id.clearOptions();

                    subdistrict_id.disable();
                    subdistrict_id.clearOptions();

                    state_id.load(function(callback) {
                        xhr && xhr.abort();
                        xhr = $.ajax({
                            type: 'GET',
                            dataType: 'json',
                            url: '/selectize/states/' + value ,
                            success: function(results) {
                                console.log(results);
                                state_id.enable();
                                callback(results);
                                state_id.setValue();

                            },
                            error: function() {
                                callback();
                            }
                        })
                    });
                }
            });

            $state_id = $('#state_id').selectize({
                valueField: 'id',
                labelField: 'name',
                searchField: 'name',
                options: [],
                create: false,
                onChange: function(value) {
                    if (!value.length) return;

                    city_id.disable();
                    city_id.clearOptions();

                    subdistrict_id.disable();
                    subdistrict_id.clearOptions();
                    
                    city_id.load(function(callback) {
                        xhr && xhr.abort();
                        xhr = $.ajax({
                            type: 'GET',
                            dataType: 'json',
                            url: '/selectize/cities/' + value ,
                            success: function(results) {
                                city_id.enable();
                                callback(results);
                                city_id.setValue();
                            },
                            error: function() {
                                callback();
                            }
                        })
                    });

                }
            });

            $city_id = $('#city_id').selectize({
                valueField: 'id',
                labelField: 'name',
                searchField: ['name'],
                onChange: function(value) {
                    if (!value.length) return;

                    console.log(city_id.options[value].name);

                    $("#city").val(city_id.options[value].name);

                    subdistrict_id.disable();
                    subdistrict_id.clearOptions();

                    subdistrict_id.load(function(callback) {
                        xhr && xhr.abort();
                        xhr = $.ajax({
                            type: 'GET',
                            dataType: 'json',
                            url: '/selectize/subdistricts/' + value ,
                            success: function(results) {
                                subdistrict_id.enable();
                                callback(results);
                                subdistrict_id.setValue();
                            },
                            error: function() {
                                callback();
                            }
                        })
                    });

                }
            });
            
            $subdistrict_id = $('#subdistrict_id').selectize({
                valueField: 'id',
                labelField: 'name',
                searchField: ['name']
            });

            country_id = $country_id[0].selectize;
            state_id = $state_id[0].selectize;
            city_id  = $city_id[0].selectize;
            subdistrict_id  = $subdistrict_id[0].selectize;

            country_id.setValue();

        });
